import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  container: {
    padding: '48px 40px',
    position: 'relative',

    [theme.breakpoints.down('sm')]: {
      padding: '0 20px',
    },
  },
}));

export default useStyles;
