import React, { useState } from 'react';

import { Typography, Grid, Container } from '@material-ui/core';

import Title from '../../elements/Title';
import RecoveryForm from './RecoveryForm';

import useStyles from './styled';

const SettingsChangePage = () => {
  const classes = useStyles();
  const [success, setSuccess] = useState(false);
  return (
    <Container maxWidth="md" style={{ marginLeft: 0 }}>
      <Grid container direction="row" justify="center" alignItems="center" className={classes.container}>
        <Title>Забыли свой пароль?</Title>
        <Typography className={classes.text}>
          Введите свой e-mail адрес. На него будет отправлено письмо, с помощью которого вы сможете сбросить пароль
        </Typography>
        <RecoveryForm
          label="Основной"
          placeholder="Логин (e-mail)"
          resetTitle="Сбросить пароль"
          cancelTitle="Отмена"
          success={success}
          setSuccess={setSuccess}
        />
      </Grid>
    </Container>
  );
};

export default SettingsChangePage;
