import React from 'react';

import { Grid } from '@material-ui/core';

import AuthLayout from '../../../../elements/AuthLayout';
import Action from '../../elements/Action';
import Divider from '../../elements/Divider';
import Form from '../../elements/Form';
import Title from '../../elements/Title';
import LoginButtons from '../../elements/LoginButtons';
import LinkWithSubtitle from '../../elements/LinkWithSubtitle';

import RegisterImage from '../../assets/register-pic.svg';
import GoogleIcon from '../../assets/google.svg';
import FacebookIcon from '../../assets/facebook.svg';

import useWindowSize from '../../../../hooks/useWindowSize';

import useStyles from './styled';

const AuthSignUpPage = () => {
  const classes = useStyles();
  const size = useWindowSize();

  const actionButtons = [
    {
      title: size.width > 1280 ? 'Регистрация через Google' : 'Через Google',
      icon: <GoogleIcon classes={classes.icons} />,
    },
    {
      title: size.width > 1280 ? 'Регистрация через Facebook' : 'Через Facebook',
      icon: <FacebookIcon classes={classes.icons} />,
    },
  ];

  return (
    <AuthLayout
      view="signup"
      image={<RegisterImage className={classes.image} />}
      action={<Action subtitle="Уже есть аккаунт?" buttonTitle="Войти" />}
    >
      <Grid container direction="row" justify="center" alignItems="center" className={classes.container}>
        <Title>Регистрация</Title>
        <LoginButtons actionButtons={actionButtons} />
        <Divider>или</Divider>
        <Form
          firstPlaceholder="E-mail"
          secondPlaceholder="Пароль не менее 8 символов"
          checkboxNode={<LinkWithSubtitle subtitle="Я согласен с" title="условиями сервиса" href="#" />}
          mainButtonTitle="Зарегистрироваться"
          padded
        />
        <LinkWithSubtitle subtitle="Уже есть аккаунт?" title="Войдите" href="#" className={classes.footer} />
      </Grid>
    </AuthLayout>
  );
};

export default AuthSignUpPage;
