import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  text: {
    margin: '42px 0 32px 0',
    width: '100%',

    [theme.breakpoints.down('sm')]: {
      margin: '20px 0 34px 0',
    },
  },
  title: {
    width: '100%',
    '&> p': {
      [theme.breakpoints.down('sm')]: {
        fontSize: '26px',
        lineHeight: '36px',
      },
    },
  },
  container: {
    padding: '48px 40px',
    position: 'relative',

    [theme.breakpoints.down('sm')]: {
      padding: '36px 0px',
    },
  },
}));

export default useStyles;
