import React, { useState } from 'react';

import { Grid, Container } from '@material-ui/core';

import Title from '../../elements/Title';
import RecoveryForm from './RecoveryForm';

import useStyles from './styled';

const SettingsChangePage = () => {
  const classes = useStyles();
  const [success, setSuccess] = useState(false);

  return (
    <Container maxWidth="md" style={{ marginLeft: 0 }}>
      <Grid container direction="row" justify="center" alignItems="center" className={classes.container}>
        <Title className={classes.title}>Сменить пароль</Title>
        {!success && (
          <RecoveryForm
            firstLabel="Новый пароль"
            secondLabel="Новый пароль (ещё раз)"
            changeTitle="Сменить пароль"
            setSuccess={setSuccess}
          />
        )}
      </Grid>
    </Container>
  );
};

export default SettingsChangePage;
