import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  text: {
    margin: '42px 0 32px 0',
    width: '100%',
  },
  container: {
    // padding: '48px 64px',
    position: 'relative',

    [theme.breakpoints.down('sm')]: {
      padding: '0 20px',
    },
  },
}));

export default useStyles;
