import { createMuiTheme } from '@material-ui/core/styles';

import breakpoints from './breakpoints';
import spacing from './spacing';
import palette from './palette';
import typography from './typography';

const theme = createMuiTheme({
  breakpoints,
  palette,
  typography,
  spacing,
  overrides: {
    MuiContainer: {
      maxWidthLg: {
        paddingLeft: spacing(5),
        paddingRight: spacing(5),
        [breakpoints.up('lg')]: {
          paddingLeft: 0,
          paddingRight: 0,
          maxWidth: 1136,
        },
        [breakpoints.up('xl')]: {
          maxWidth: 1232,
        },
      },
      maxWidthMd: {
        width: '100%',
        paddingLeft: spacing(5),
        paddingRight: spacing(5),
        maxWidth: 896,
        [breakpoints.up('md')]: {
          maxWidth: 896,
        },
      },
    },
    MuiButton: {
      startIcon: {
        width: 16,
        height: 16,
        '& svg': {
          maxWidth: '100%',
          maxHeight: '100%',
        },
      },
      sizeLarge: {
        padding: spacing(4),
        borderRadius: 8,
        fontSize: 14,
      },
    },
    MuiInput: {
      input: {
        fontSize: 16,
        lineHeight: '24px',
        color: '#011136',
        caretColor: '#165EFE',
        '&::placeholder': {
          color: '#6D7885',
          opacity: 1,
        },
      },
      underline: {
        '&:before, &:after': {
          display: 'none',
        },
      },
      root: {
        height: 24,
      },
    },
