          backgroundColor: 'rgba(239, 64, 86, 0.03)',
          borderColor: '#EF4056',
        },
      },
      underline: {
        '&:before, &:after': {
          display: 'none',
        },
      },
    },
    MuiCheckbox: {
      root: {
        padding: spacing(3),
      },
    },
    MuiSelect: {
      select: {
        border: '0.5px solid rgba(1, 17, 54, 0.12)',
        borderRadius: 8,
        paddingLeft: spacing(3),
        height: 48,
        backgroundColor: '#ffffff',
        '&:focus': {
          borderRadius: 8,
          backgroundColor: '#ffffff',
        },
      },
      icon: {
        right: spacing(3),
      },
    },
    MuiTooltip: {
      tooltip: {
        backgroundColor: '#011136',
        fontSize: 14,
        lineHeight: '20px',
        letterSpacing: '0.15px',
        textAlign: 'center',
      },
      arrow: {
        color: '#011136',
      },
    },
    MuiExpansionPanel: {
      root: {
        borderTop: '1px solid #D8DADE',
        background: '#FBFBFB',
        boxShadow: 'none',
        '&.Mui-expanded': {
          margin: 0,
        },
        '&:before': {
          opacity: 0,
        },
      },
    },
    MuiExpansionPanelSummary: {
      root: {
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: spacing(5),
        paddingRight: spacing(5),
        [breakpoints.up('lg')]: {
          paddingLeft: 0,
          paddingRight: 0,
          maxWidth: 1136,
        },
        [breakpoints.up('xl')]: {
          maxWidth: 1232,
        },
      },
      content: {
        marginTop: 0,
        marginBottom: 0,
        '&.Mui-expanded': {
          margin: 0,
        },
      },
    },
    MuiGridListTile: {
      tile: {
        overflow: 'visible',
      },
    },
  },
  shadows: [
    'none',
    '0px 1px 2px rgba(1, 17, 54, 0.24), 0px 1px 2px rgba(1, 17, 54, 0.12), 0px 0px 1px rgba(1, 17, 54, 0.16)',
    '0px 1px 8px rgba(1, 17, 54, 0.1), 0px 2px 4px rgba(1, 17, 54, 0.12), 0px 1px 2px rgba(1, 17, 54, 0.16)',
    '0px 4px 4px rgba(1, 17, 54, 0.08), 0px 0px 4px rgba(1, 17, 54, 0.06)',
