import React, { memo } from 'react';

import cx from 'classnames';
import { Grid, Input, FormControlLabel, Checkbox, Button } from '@material-ui/core';

import useWindowSize from '../../../../hooks/useWindowSize';
import useStyles from './styled';

const renderSubButton = ({ classes, subtitle }) => (
  <Button size="large" color="primary" className={classes.subButton}>
    {subtitle}
  </Button>
);

const Form = ({
  subtitle,
  firstPlaceholder,
  secondPlaceholder,
  checkboxNode,
  checkboxNodeMobile,
  mainButtonTitle,
  padded,
}) => {
  const classes = useStyles();
  const size = useWindowSize();

  return (
    <Grid container direction="column">
      <Input variant="outlined" placeholder={firstPlaceholder} className={classes.input} />
      <Input variant="outlined" placeholder={secondPlaceholder} className={classes.input} />
      <Grid container direction="row" justify="space-between" className={classes.footerForm}>
        <FormControlLabel
          control={<Checkbox color="primary" />}
          label={size.width > 1280 ? checkboxNode : checkboxNodeMobile || checkboxNode}
          className={classes.checkboxLabel}
        />
        {subtitle && <div className={classes.subButtonDesktop}>{renderSubButton({ classes, subtitle })}</div>}
      </Grid>
      <Grid container direction="column" className={classes.footerForm}>
        {subtitle && <div className={classes.subButtonMobile}>{renderSubButton({ classes, subtitle })}</div>}
        <Button
          variant="contained"
          size="large"
          color="primary"
          className={cx(classes.loginButton, padded && classes.padded)}
        >
          {mainButtonTitle}
        </Button>
      </Grid>
    </Grid>
  );
};

export default memo(Form);
