import React from 'react';

import { Grid, Container } from '@material-ui/core';

import Title from './Title';
import Subtitle from './Subtitle';

import EnvelopeImage from '../assets/envelope.png';
import CheckEmailImage from '../assets/check-email.svg';

import useStyles from './styled';

const ConfirmationMailPage = () => {
  const classes = useStyles();
  return (
    <Container>
      <Grid container direction="column" justify="flex-start" alignItems="center" className={classes.container}>
        <Title>
          <img src={EnvelopeImage} alt="Envelope" style={{ width: 36 }} />
          <br />
          Проверьте свою почту
        </Title>
        <Subtitle>
          На указанный при регистрации почтовый адрес отправлено письмо с ссылкой для подтверждения почты.
        </Subtitle>
        <CheckEmailImage className={classes.image} />
      </Grid>
    </Container>
  );
};

export default ConfirmationMailPage;
