import React, { memo } from 'react';
import PropTypes from 'prop-types';

import cx from 'classnames';
import { ThemeProvider } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';

import theme from '../../theme';
import LogoIcon from './assets/logo.svg';
import useStyles from './styled';

const AuthLayout = ({ view, image, action, children }) => {
  const classes = useStyles();

  const vh = window.innerHeight * 0.01;
  document.documentElement.style.setProperty('--vh', `${vh}px`);

  return (
    <ThemeProvider theme={theme}>
      <Grid
        container
        direction={view === 'signup' ? 'row-reverse' : 'row'}
        justify="center"
        alignItems="center"
        className={classes.authLayout}
      >
        <Box className={classes.sideBox}>
          <LogoIcon style={{ marginTop: 2 }} />
          <Box className={cx([classes.centerBox])}>{children}</Box>
        </Box>
        <Box className={cx([classes.sideBox, classes.centerBox, classes.coloredBackground])}>
          {image}
          {action}
        </Box>
      </Grid>
    </ThemeProvider>
  );
};

AuthLayout.defaultProps = {
  view: 'signin',
  image: null,
  action: null,
  children: null,
};

AuthLayout.propTypes = {
  view: PropTypes.string,
  image: PropTypes.node,
  action: PropTypes.node,
  children: PropTypes.node,
};

export default memo(AuthLayout);
