import React, { memo } from 'react';

import cx from 'classnames';
import { Grid, Box, Input, Button } from '@material-ui/core';

import useStyles from './styled';

const RecoveryForm = ({ text, placeholder, resetTitle, cancelTitle }) => {
  const classes = useStyles();

  return (
    <Grid container direction="column">
      <Input variant="outlined" placeholder={placeholder} className={classes.input} />
      {text}
      <Grid container direction="column" className={classes.footerForm}>
        <Button variant="contained" size="large" color="primary" className={cx(classes.loginButton)}>
          <Box>{resetTitle}</Box>
        </Button>
        <Button variant="contained" size="large" color="primary" className={cx(classes.loginButton)}>
          <Box>{cancelTitle}</Box>
        </Button>
      </Grid>
    </Grid>
  );
};

export default memo(RecoveryForm);
