import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  title: {
    ...theme.typography.h2,
    textAlign: 'center',
    color: theme.palette.text.primary,
    marginBottom: 14,
    [theme.breakpoints.down('sm')]: {
      ...theme.typography.h3,
      fontSize: '22px',
      lineHeight: '32px',
    },
  },
}));

export default useStyles;
