import React, { memo } from 'react';

import cx from 'classnames';
import { Grid, Box, Input, Button } from '@material-ui/core';

import useWindowSize from '../../../../hooks/useWindowSize';
import useStyles from './styled';

const RecoveryForm = ({ firstPlaceholder, mainButtonTitle, padded }) => {
  const classes = useStyles();
  const size = useWindowSize();

  return (
    <Grid container direction="column">
      <Input variant="outlined" placeholder={firstPlaceholder} className={classes.input} />
      <Grid container direction="column" className={classes.footerForm}>
        <Button
          variant="contained"
          size="large"
          color="primary"
          className={cx(classes.loginButton, size.width < 1279 && classes.withoutSpacing, padded && classes.padded)}
        >
          <Box>{mainButtonTitle}</Box>
        </Button>
      </Grid>
    </Grid>
  );
};

export default memo(RecoveryForm);
