import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => {
  return {
    contentTitle: {
      ...theme.typography.h2,
      color: theme.palette.text.primary,
      [theme.breakpoints.down('md')]: {
        textAlign: 'center',
      },
    },
  };
});

export default useStyles;
