import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => {
  return {
    container: {
      marginTop: 80,
      [theme.breakpoints.down('md')]: {
        marginTop: 24,
      },
    },
    text: {
      margin: '42px 0 32px 0',
      width: '100%',
    },
  };
});

export default useStyles;
