import React, { memo } from 'react';

import cx from 'classnames';
import { Grid, Box, Input, Button, Typography } from '@material-ui/core';

import useStyles from './styled';

const RecoveryForm = ({ label, placeholder, resetTitle, cancelTitle }) => {
  const classes = useStyles();
  return (
    <Grid container direction="column">
      <Typography className={classes.label}>{label}</Typography>
      <Input variant="outlined" placeholder={placeholder} className={classes.input} />
      <Grid container direction="row" className={classes.footerForm}>
        <Button variant="contained" size="large" color="primary" className={cx(classes.resetButton, classes.button)}>
          <Box>{resetTitle}</Box>
        </Button>
        <Button color="primary" size="large" className={cx(classes.cancelButton, classes.button)}>
          <Box>{cancelTitle}</Box>
        </Button>
      </Grid>
    </Grid>
  );
};

export default memo(RecoveryForm);
