import React from 'react';

import { Grid } from '@material-ui/core';

import AuthLayout from '../../../../elements/AuthLayout';
import Action from '../../elements/Action';
import Divider from '../../elements/Divider';
import Form from '../../elements/Form';
import Title from '../../elements/Title';
import LoginButtons from '../../elements/LoginButtons';
import LinkWithSubtitle from '../../elements/LinkWithSubtitle';

import LoginImage from '../../assets/login-pic.svg';
import GoogleIcon from '../../assets/google.svg';
import FacebookIcon from '../../assets/facebook.svg';

import useStyles from './styled';

const AuthSignInPage = () => {
  const classes = useStyles();

  const actionButtons = [
    {
      title: () => (
        <>
          <div className={classes.showDesktop}>Войти через Google</div>
          <div className={classes.showMobile}>Через Google</div>
        </>
      ),
      icon: <GoogleIcon classes={classes.icons} />,
    },
    {
      title: () => (
        <>
          <div className={classes.showDesktop}>Войти через Facebook</div>
          <div className={classes.showMobile}>Через Facebook</div>
        </>
      ),
      icon: <FacebookIcon classes={classes.icons} />,
    },
  ];

  return (
    <AuthLayout
      view="signin"
      image={<LoginImage className={classes.image} />}
      action={<Action subtitle="У вас ещё нет аккаунта?" buttonTitle="Зарегистрироваться" />}
    >
      <Grid container direction="row" justify="center" alignItems="center" className={classes.container}>
        <Title>Вход</Title>
        <LoginButtons actionButtons={actionButtons} />
        <Divider>или</Divider>
        <Form
          subtitle="Забыли пароль?"
          firstPlaceholder="Логин (e-mail)"
          secondPlaceholder="Пароль"
          checkboxNode="запомнить меня"
          checkboxNodeMobile="запомнить"
          mainButtonTitle="Войти"
        />
        <LinkWithSubtitle
          subtitle="У вас ещё нет аккаунта?"
          title="Зарегистрируйтесь"
          href="#"
          className={classes.footer}
        />
      </Grid>
    </AuthLayout>
  );
};

export default AuthSignInPage;
