import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  title: {
    [theme.breakpoints.down('sm')]: {
      fontSize: 26,
      lineHeight: 36,
    },
  },
  text: {
    margin: '42px 0 32px 0',
    width: '100%',

    [theme.breakpoints.down('sm')]: {
      padding: '42px 0 32px 0',
    },
  },
  container: {
    padding: '48px 40px',
    position: 'relative',

    [theme.breakpoints.down('sm')]: {
      padding: '36px 0px',
    },
  },
}));

export default useStyles;
