import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  image: {
    width: '100%',
    height: '100%',
    maxWidth: 871,
    maxHeight: 468,
  },
  container: {
    marginTop: theme.spacing(7),
  },
  icon: {
    width: 36,
    [theme.breakpoints.down('sm')]: {
      width: 22,
    },
  },
}));

export default useStyles;
