import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  title: {
    ...theme.typography.h2,
    [theme.breakpoints.down('sm')]: {
      ...theme.typography.h3,
    },
  },
}));

export default useStyles;
